import React, { useState } from 'react';
import { motion } from 'framer-motion';
import Loader from './Loader';

const ColorSearch = () => {
  const [hexInput, setHexInput] = useState('');
  const [colorData, setColorData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const searchColor = async () => {
    if (!hexInput.trim()) return;

    // Remove # if present and validate hex
    const hex = hexInput.replace('#', '').toUpperCase();
    if (!/^[0-9A-F]{6}$/.test(hex)) {
      setError('Please enter a valid 6-digit hex code');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const response = await fetch(`https://www.colourlovers.com/api/color/${hex}?format=json`);
      const data = await response.json();

      if (data && data.length > 0) {
        setColorData(data[0]);
      } else {
        setError('Color not found');
      }
    } catch (err) {
      setError('Failed to fetch color data');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    searchColor();
  };

  return (
    <div className="mb-12">
      <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-6">
        Search Color
      </h2>

      <form onSubmit={handleSubmit} className="mb-6">
        <div className="flex gap-2">
          <input
            type="text"
            value={hexInput}
            onChange={(e) => setHexInput(e.target.value)}
            placeholder="Enter hex code (e.g., FF5733)"
            className="flex-1 px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-lg bg-white dark:bg-gray-800 text-gray-900 dark:text-white focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          />
          <button
            type="submit"
            disabled={loading}
            className="px-6 py-2 bg-blue-600 hover:bg-blue-700 disabled:bg-blue-400 text-white rounded-lg transition-colors"
          >
            Search
          </button>
        </div>
      </form>

      {error && (
        <div className="text-red-600 dark:text-red-400 mb-4">{error}</div>
      )}

      {loading && <Loader />}

      {colorData && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6"
        >
          <div className="flex items-center gap-4 mb-4">
            <div
              className="w-16 h-16 rounded-lg shadow-md"
              style={{ backgroundColor: `#${colorData.hex}` }}
            />
            <div>
              <h3 className="text-xl font-semibold text-gray-900 dark:text-white">
                {colorData.title}
              </h3>
              <p className="text-gray-600 dark:text-gray-300">
                #{colorData.hex}
              </p>
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4 text-sm">
            <div>
              <span className="font-medium text-gray-700 dark:text-gray-300">RGB:</span>
              <span className="ml-2 text-gray-600 dark:text-gray-400">
                {colorData.rgb.red}, {colorData.rgb.green}, {colorData.rgb.blue}
              </span>
            </div>
            <div>
              <span className="font-medium text-gray-700 dark:text-gray-300">HSV:</span>
              <span className="ml-2 text-gray-600 dark:text-gray-400">
                {colorData.hsv.hue}Â°, {colorData.hsv.saturation}%, {colorData.hsv.value}%
              </span>
            </div>
          </div>
        </motion.div>
      )}
    </div>
  );
};

export default ColorSearch;
